-- (QUEST_ID, "Title", "Details", "Objectives", "OfferRewardText", "RequestItemsText", "EndText", "CompletedText", "QuestGiverTextWindow_loc8", "QuestGiverTargetName_loc8", "QuestTurnTextWindow_loc8", "QuestTurnTargetName_loc8"),

INSERT `locales_quest` (`Id`, `Title_loc8`, `Details_loc8`, `Objectives_loc8`, `OfferRewardText_loc8`, `RequestItemsText_loc8`, `EndText_loc8`, `CompletedText_loc8`, `QuestGiverTextWindow_loc8`, `QuestGiverTargetName_loc8`, `QuestTurnTextWindow_loc8`, `QuestTurnTargetName_loc8`) VALUES
(3117, "Выгравированный знак", "Этот знак был передан мне посланником нашей наставницы охотников, Аянны. Похоже, она хотела бы поговорить с тобой, когда у тебя будет время. Изучи знак и отправляйся к Аянне.", "Поговорите с Аянной Вечной Странницей в Альдрассиле, что в Тенистой долине.", "Когда обретешь достаточно опыта в Тенистой долине, приходи ко мне – я помогу тебе постичь умения охотника и осознать твои собственные возможности.$B$BЭто опасные времена, $n, и даже умения твоих питомцев нельзя принимать как должное – в мире существуют вещи куда более опасные, нежели ты можешь вообразить. Да хранит тебя Элуна, $n, я буду ждать твоего возвращения.", "Ты наконец-то здесь, $n, хотя и не так скоро, как я надеялась. Добро пожаловать в Тенистую долину.$B$BНадеюсь, ты $gполучил:получила; мое послание, будучи в добром здравии и расположении духа?$B$BКак я говорила ранее, я намереваюсь обучить тебя охотничьему мастерству: приручению зверей, которые помогут тебе в бою, стрельбе из лука с безукоризненной меткостью и уважению к землям, которые мы зовем домом, так и к тем, что лежат за очерченными нами пределами.", "", "", "", "", "", ""),
(26947, "Обучение лесного жителя", "Говоря о калдорай, большинство имеет в виду друидов или часовых, но путь охотника не менее древний и почетный.$B$BЯ наблюдала за тобой и вижу, что у тебя есть кое-какой опыт в обращении с луком. По мере накопления умений тебе будут открываться новые возможности.$B$BЯ хочу, чтобы ты $gполучил:получила; новый опыт и $gвыяснил:выяснила;, как делать что-то новое. Затем используй новые умения на тренировочном манекене неподалеку от юго-восточной окраины Альдрассила.", "Достигните 3-го уровня, чтобы изучить способность \"Верный выстрел\". Найдите тренировочный манекен к юго-востоку от Альдрассила и опробуйте на нем эту способность пять раз.", "Ты быстро постигаешь нашу науку.", "", "", "Вернитесь к Аянне Вечной Страннице в Альдрассил.", "", "", "", ""),
(28713, "Природное равновесие", "Привет, $n. Мое имя Илталайн. В Тенистой долине я учу таких, как ты, и забочусь о том, чтобы не нарушалось природное равновесие.$B$BВ этом году весна была очень дождливой, и в результате одни звери чувствуют себя прекрасно, а другие, наоборот, страдают. К сожалению, ночные саблезубы слишком расплодились: если ничего не предпринять, они истребят всю живность в этом лесу.$B$BИди же, |3-6($c), и уменьши численность саблезубов, чтобы в природе восстановилась гармония.", "Убейте 6 молодых ночных саблезубов.", "Ты хорошо исполняешь свой долг, $n.", "Еще многое предстоит сделать, $n. Возвращайся ко мне, как только ты сократишь численность ночных саблезубов.", "", "Поговорите с Илталайном в Тенистой долине.", "", "", "", ""),
(28714, "Мох скверны", "Похоже, ты знаешь, что делаешь. Значит, я могу дать тебе более важное поручение.$B$BНасколько тебе известно, Тельдрассил был очищен от порчи. Но все-таки там что-то осталось. Звери не успокоились, и местных жителей одолевают разные беды.$B$BИди на запад и разберись с тамошними греллями. Проверь, нет ли у них мха скверны. Это поможет мне понять, откуда здесь берется скверна.", "Соберите 6 пучков мха Скверны, отобрав их у греллей, которые находятся к западу от Илталайна.", "То, что ты $gсделал:сделала; для созданий Тенистой долины, достойно награды, $n.$B$BОднако ты $gубедил:убедила; меня, что мои страхи не были беспочвенными. Несмотря на благословение Тельдрассила, грелли все еще страдают от мха Скверны. В этом дереве таится нечто ужасное. Мне остается надеяться только на то, что племя фурболгов Кривой Сосны не затронуто порчей, или всем нам грозит большая опасность.$B$BЯ посвящу еще некоторое время изучению этого вопроса и свяжусь с теми, кто может помочь. Спасибо тебе, |3-6$c.", "Подтверди мои опасения, $n. Принеси мне мох Скверны от греллей.", "", "Вернитесь к Илталайну в Тенистую долину.", "", "", "", ""),
(28715, "Демонические воры", "Мерзкие грелли! Они снова воруют наши припасы.$B$BЯ почти уверен в том, что они спрятали мои сумки в своем лагере, к западу отсюда. Принеси мне эти сумки, и я с радостью награжу тебя.", "Найдите украденные у Мелитара сумки на западе, рядом с греллями.", "Слава Элуне! И тебе тоже, |3-6$c.$B$BЭти сумки нужны многим молодым ночным эльфам, но я с радостью дам тебе одну в качестве награды. Думаю, она тебе пригодится.$B$BСчастливого пути.", "У меня не будет провианта для покупателей, пока мне не вернут украденное добро. Ну как, удалось?", "", "Поговорите с Мелитаром Оленьим Шлемом в Альдрассиле.", "", "", "", ""),
(28723, "Жрица луны", "Теперь, когда ты уже умеешь применять свою новую способность, возможно, ты поможешь очистить Тенистую долину от порчи.$B$BДентария Серебристая Поляна, жрица Луны, предложила нам свою помощь. Она находится между двумя водоемами, к северу от нашего великого дерева. Обойди дерево, |3-6($c), найди жрицу и предложи ей свою помощь.", "Поговорите с Дентарией Серебристой Поляной в центре Тенистой долины между двумя водоемами.", "Хорошо, что ты здесь. Похоже, процесс обучения идет довольно быстро.", "", "", "", "", "", "", ""),
(28724, "Противоядие для Иверрона", "Нашего друга Иверрона покусали местные пауки и отравили его своим ядом. Я могу поддерживать Иверрона в его нынешнем состоянии, но для приготовления противоядия мне нужна твоя помощь.$B$BПо берегам этих водоемов растут лунные лилии. Они обладают целебными свойствами, и я могу объединить их с другими ингредиентами и сделать противоядие для Иверрона.$B$BПожалуйста, набери этих лилий. В здешних краях их много, можно увидеть даже отсюда.", "Соберите 7 лунных лилий.", "Это то, что нужно. Спасибо, $n. Ты действуешь очень быстро, и это прекрасно.", "Наш дорогой друг чувствует себя не очень хорошо, и даже мои лекарства не помогают. Тебе удалось добыть нужные ингредиенты?", "", "Поговорите с Дентарией Серебристой Поляной между водоемов в Тенистой долине.", "", "", "", ""),
(28734, "Просьба Мелитара", "Тебе нужно поговорить с Мелитаром. Кажется, у него уже были какие-то проблемы с греллями.$B$BЕсли ты поможешь ему, то, возможно, он выделит тебе место для хранения товаров.", "Идите в Тенистую долину и поговорите с Мелитаром Оленьим Шлемом.", "Тебя прислал Илталайн? Весьма умно с его стороны. Мне в самом деле нужна помощь.", "", "", "", "", "", "", "");
